import { Component } from '@angular/core';

@Component({
  selector: 'app-add-x',
  templateUrl: './add-x.component.html',
  styleUrls: ['./add-x.component.css']
})
export class AddXComponent {

  coleccion:any = [1,2,3,4];

  agregarElemento = () => {
    this.coleccion.push('X');
  }
}
<p>Arreglo: {{coleccion}} Tamaño: {{coleccion.length}}</p>

<button (click)="agregarElemento()">Agregar Elemento</button>
